								<h1>Disabled Triggers</h1>								<p>The disabled triggers are shown as below: </p>							</div>							<p></p>							<?php							$Oracle_conn=oci_connect("SCOTT","tiger","orcl");      							//分配并返回一个游标句柄							$cur=oci_new_cursor($Oracle_conn);							//创建调用语句							$query="call traveler_admin_package.get_disabled_triggers(:v_cur)";							$statement=oci_parse($Oracle_conn,$query);							//提供输入参数														//绑定游标句柄，接收返回的游标参数							//oci_bind_by_name($statement,":STATUS",$STATUS,16);							oci_bind_by_name($statement,":v_cur",$cur,-1,OCI_B_CURSOR);							//执行							oci_execute($statement);							//获取返回的游标数据到游标句柄							oci_execute($cur);							//遍历游标内容                                          								while ($dat = oci_fetch_row($cur)) {								echo "<blockquote><font size='3' style='LINE-HEIGHT:25px'><span style='font-weight:bold;'>Trigger name : </span>";								echo($dat[0]);								echo"</br>";								echo "<span style='font-weight:bold;'>Trigger type : </span>";								echo($dat[1]);								echo"</br>";								echo "<span style='font-weight:bold;'>Status : </span>";								echo($dat[2]);								echo"</font>";								echo"</bloackquote>";							}														oci_free_statement($statement);							oci_close($Oracle_conn);							?>						</article>					</div> 					<div id="tab-4" class="tab">						<article>							<div class="text-section">								<h1>Relations</h1>								<p>Type in an object and you will see the dependent objects related to it, </br>so you can have a better understanding of the structure of the database tables.</p>							</div>							<p></p>							<form action='relation0.php' method='post'>							<p style='text-indent:3em'>Please type in the object:</p>							<p style='text-indent:3em'><input type="text" name='object'  style="width:200px;"/></p>							</form>						</article>					</div>					<div id="tab-6" class="tab">						<article>							<div class="text-section">								<h1>Records